import React from 'react';
import { QRCodeSVG } from 'qrcode.react';
import '../../styles/Realtor/FormLinkGenerator.css';

const FormLinkGenerator = ({ generatedLink, onGenerateLink, user }) => {
  // Use window.location.origin to get the current frontend URL
  const formLink = generatedLink || `${window.location.origin}/form/${user?.id}`;

  return (
    <div className="form-link-section">
      <h3>Your Client Form</h3>
      <p>Share this link or QR code with your clients</p>
      <div className="form-link-container">
        <input
          type="text"
          value={formLink}
          readOnly
        />
        <button onClick={onGenerateLink}>Generate Link</button>
      </div>
      <div className="qr-code-container">
        <QRCodeSVG value={formLink} size={128} />
      </div>
    </div>
  );
};

export default FormLinkGenerator;
