import React, { useState, useEffect } from 'react';
import '../../styles/Realtor/GeneratedLeads.css';

const GeneratedLeads = () => {
  const [leads, setLeads] = useState({
    sellers: [],
    buyers: []
  });
  const [selectedClient, setSelectedClient] = useState(null);

  const getLeads = async () => {
    try {
      const response = await fetch("https://realtoriqbackend.onrender.com/api/clients", {
        credentials: 'include',
      });

      if (response.ok) {
        const clients = await response.json();
        console.log("Fetched clients:", clients);
        setLeads({
          sellers: clients.filter(client => client.client_type === 'seller'),
          buyers: clients.filter(client => client.client_type === 'buyer')
        });
      }
    } catch (error) {
      console.error("Error fetching leads:", error);
    }
  };

  useEffect(() => {
    getLeads();
    const intervalId = setInterval(getLeads, 5000);
    return () => clearInterval(intervalId);
  }, []);

  const handlePinToggle = async (clientId, currentPinned) => {
    try {
      const response = await fetch(`https://realtoriqbackend.onrender.com/api/clients/${clientId}/pin`, {
        method: 'PUT',
        credentials: 'include',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ pinned: !currentPinned }),
      });

      if (response.ok) {
        getLeads(); // Refresh the leads after pinning/unpinning
      }
    } catch (error) {
      console.error("Error toggling pin:", error);
    }
  };

  const LeadCard = ({ client, type }) => (
    <div className="lead-card">
      <div className="lead-header">
        <div className="lead-title">
          <span className={`lead-type ${type}`}>{type}</span>
          <strong>{client.first_name} {client.last_name}</strong>
        </div>
        <button
          className="pin-button"
          onClick={() => handlePinToggle(client.id, client.pinned)}
        >
          {client.pinned ? 'Unpin' : 'Pin'}
        </button>
      </div>
      <div className="lead-info">
        <div className="lead-details">
          <p><i className="fas fa-calendar"></i> Created: {new Date(client.created_at).toLocaleString()}</p>
          <p><i className="fas fa-phone"></i> {client.phone}</p>
          <p><i className="fas fa-envelope"></i> {client.email}</p>
          {type === 'seller' ? (
            <>
              <p><i className="fas fa-home"></i> Property Type: {client.property_type}</p>
              <p><i className="fas fa-tag"></i> Notes: {client.notes}</p>
              {client.property_images && client.property_images.length > 0 && (
                <div className="property-images">
                  {client.property_images.map((url, idx) => (
                    <img src={url} alt={`property-${idx}`} key={idx} />
                  ))}
                </div>
              )}
            </>
          ) : (
            <>
              <p><i className="fas fa-wallet"></i> Budget: ${client.budget}</p>
              <p><i className="fas fa-map-marker-alt"></i> Location: {client.location}</p>
              <p><i className="fas fa-list"></i> Amenities: {client.amenities}</p>
            </>
          )}
        </div>
      </div>
    </div>
  );

  return (
    <section className="generated-leads">
      <div className="leads-header">
        <h2>Client Leads</h2>
        <p>All your client inquiries in one place</p>
      </div>

      <div className="leads-grid">
        <div className="leads-column">
          <div className="leads-section-header">
            <h3>Seller Clients</h3>
            <span className="lead-count">{leads.sellers.length} clients</span>
          </div>
          <div className="leads-list">
            {leads.sellers.length > 0 ? (
              leads.sellers.map((client) => (
                <LeadCard key={client.id} client={client} type="seller" />
              ))
            ) : (
              <div className="no-leads">No seller clients yet</div>
            )}
          </div>
        </div>

        <div className="leads-column">
          <div className="leads-section-header">
            <h3>Buyer Clients</h3>
            <span className="lead-count">{leads.buyers.length} clients</span>
          </div>
          <div className="leads-list">
            {leads.buyers.length > 0 ? (
              leads.buyers.map((client) => (
                <LeadCard key={client.id} client={client} type="buyer" />
              ))
            ) : (
              <div className="no-leads">No buyer clients yet</div>
            )}
          </div>
        </div>
      </div>
    </section>
  );
};

export default GeneratedLeads;
